import xbmc
import urllib2
import threading
from quasar.config import QUASARD_HOST
from quasar.daemon import shutdown


class QuasarMonitor(xbmc.Monitor):
    def __init__(self):
        self._closing = threading.Event()

    @property
    def closing(self):
        return self._closing

    def onAbortRequested(self):
        # Only when closing Kodi
        if xbmc.abortRequested:
            xbmc.executebuiltin("Dialog.Close(all, true)")
            shutdown()
            self._closing.set()
            self._closing.clear()

    def onSettingsChanged(self):
        try:
            urllib2.urlopen("%s/reload" % QUASARD_HOST)
            urllib2.urlopen("%s/cmd/clear_cache" % QUASARD_HOST)
        except:
            pass
